(function(){tinymce.PluginManager.requireLangPack('hljs');tinymce.create('tinymce.plugins.hljs',{init:function(ed,url){ed.addCommand('mceaddhljs',function(){ed.windowManager.open({file:url+'/hljs.htm',width:530+ed.getLang('hljs.delta_width',0),height:500+ed.getLang('hljs.delta_height',0),inline:1},{plugin_url:url,some_custom_arg:'custom arg'})});ed.addButton('hljs',{title:'hljs.hljs_button_desc',cmd:'mceaddhljs',image:url+'/img/hljs.gif'});ed.onNodeChange.add(function(ed,cm,n){cm.setActive('hljs',n.nodeName=='IMG')})},getInfo:function(){return{longname:'hljs',author:'Ron Wardenier',authorurl:'http://wardenier.com/',infourl:'mailto://ron@wardenier.com',version:"1.0"}}});tinymce.PluginManager.add('hljs',tinymce.plugins.hljs)})();